<?phpnamespace controllers;use models\auth;include '../model/auth.php';class authController{    public function login():void    {        $fields = [            'username' => 'username is required',            'password' => 'password is required',            'rule' => 'please select your role'        ];        $data = [];        $error = [];        foreach ($fields as $field => $message){            if (!isset($_POST[$field]) || trim($_POST[$field]) === ''){                $error[$field] = $message;            }else{                $data[$field] = htmlspecialchars($_POST[$field]);            }        }        if (!empty($error)){            $data['success'] = false;            $data['errors'] = $error;            echo json_encode($data);            return;        }        $user = new \model\auth();        $user->login(            $data['username'],            $data['password'],            $data['rule']        );    }}if ($_SERVER['REQUEST_METHOD'] === 'POST' && isset($_POST['action'])){    $login = new authController();    switch ($_POST['action']){        case 'login':            $login->login();            break;        default:            echo 'Method is Not Allowed';    }}