<?phpnamespace controllers;require_once  '../model/booking.php';require_once '../../Helper/ErrorMessage.php';use Exception;use model\booking;class bookingController{    public function storeBooking():void    {        $Fields = [            'name' => 'name is required',            'StudentId' => 'Student Id is required',            'email'  => 'email is required',            'date'  => 'date is required',        ];        $data = [];        $error = [];        foreach($Fields as $Field => $message){            if (!isset($_POST[$Field]) || trim($_POST[$Field]) === '') { // check if each input is Empty or no value                $error[$Field] = $message;            }else{                $data[$Field] = htmlspecialchars($_POST[$Field]);            }        }        if (!empty($error)) { // check if the error array is not empty            $data['success'] = false;            $data['errors'] = $error;            echo json_encode($data);            return;        }        $booking = new booking();        $booking->store(            $data['name'],            $data['date'],            $data['email'],            $data['StudentId'],        );    }}if ($_SERVER['REQUEST_METHOD'] === 'POST' && isset($_POST['action'])){    match ($_POST['action']){        'store' => (new bookingController())->storeBooking(),        default => http_response_code(400)    };}