<?phpnamespace model;require_once '../../config/Connection.php';require_once '../../Helper/ErrorMessage.php';class auth extends \Connection{    public function login($username, $password, $role): void    {        $query = "SELECT * FROM admin WHERE username = ? AND role = ?";        $stmt = $this->Connect()->prepare($query);        $stmt->bind_param('ss', $username, $role);        $stmt->execute();        $result = $stmt->get_result();        if ($result->num_rows > 0) {            $admin = $result->fetch_assoc();            if (password_verify($password, $admin['password'])) {                // Start session after successful login                session_start();                $_SESSION['username'] = $username;                $_SESSION['role'] = $role;                \ErrorMessage::JsonResponse(true, 'success login '. $role);            } else {                echo json_encode(['success' => false, 'message' => 'Invalid Username or Password']);            }        } else {            echo json_encode(['success' => false, 'message' => 'Account Not Found']);        }    }}