<?phpnamespace model;require_once '../../config/Connection.php';require_once '../../Helper/ErrorMessage.php';use Exception;class booking extends \Connection{  public function store(string $name, string $date, string $email , string $studentId):void  {      try {          $query = "INSERT INTO booking(studentName, bookingDate, email, studentId) VALUES(?,?,?,?)";          $stmt = $this->Connect()->prepare($query);          if (!$stmt){              \ErrorMessage::JsonResponse(false,'Failed to Prepared Statement');              return;          }          $stmt->bind_param('ssss',$name,$date,$email,$studentId);          if ($stmt->execute()){              \ErrorMessage::JsonResponse(true,'Successfully booked');          }      }catch (Exception $e){          error_log('Error'. $e->getMessage());      }  }}